,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select apex_util.prepare_url(''f?p=''||:APP_ID||'':7:''||:APP_SESSION||'':::7:P7_WORKTOOL_ID:''|| SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_ID) card_link,',
