'    SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_TYPE card_title,',
'    ',
'    SEC_HD_TICKETS.TICKET_STATUS card_text,',
'',
'    SEC_HD_TICKETS.STAFF_ID card_extra,',
'',
'    SEC_HD_TICKETS.TICKET_STATUS_DATE card_subtext,',
'    ',
'    case SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_TYPE',
'    WHEN ''TELEVISION'' THEN ''fa-television''',
'    WHEN ''DESKTOP'' THEN ''fa-desktop''',
'    WHEN ''LAPTOP'' THEN ''fa-laptop''',
'    WHEN ''PRINTER'' THEN ''fa-print''',
'    WHEN ''PHOTOCOPIER'' THEN ''fa-tabs''',
'    WHEN ''SCANNER'' THEN ''fa-minus-square-o''',
'    WHEN ''FAXER'' THEN ''fa-fax''',
'    WHEN ''SHREDDER'' THEN ''fa-file-x''',
'    WHEN ''PHONE'' THEN ''fa-office-phone''',
'    WHEN ''MOUSE'' THEN ''fa-mouse-pointer''',
'    WHEN ''KEYBOARD'' THEN ''fa-keyboard-o''',
'    END CARD_ICON,',
'    ',
'  case SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_STATUS ',
'    when ''PENDING'' then ''fa fa-clock-o fa-2x''',
'    when ''RESOLVED'' then ''fa fa-check-circle-o fa-2x''',
'    when ''RETRIEVED'' then ''fa fa-exclamation-circle fa-2x''',
'    when ''OUT FOR REPAIRS'' then ''fa fa-exclamation-triangle fa-2x''',
'    END CARD_ICON_EXTRA,    ',
'',
'    SEC_HD_TICKETS.TICKET_STATUS card_initials',
'',
'from SEC_HD_TICKETS, SEC_HD_WORKTOOL_INFORMATION, SEC_HD_WORKTOOL_ALLOCATIONS, SEC_HD_DEPARTMENTS, SEC_HD_DIVISIONS',
'',
'  where TICKET_STATUS != ''RESOLVED''',
'    AND TICKET_STATUS != ''PENDING''',
'    AND SEC_HD_TICKETS.WORKTOOL_ID = SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_ID',
'    AND SEC_HD_TICKETS.WORKTOOL_ID = SEC_HD_WORKTOOL_ALLOCATIONS.WORKTOOL_ID',
'    AND SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_ID = SEC_HD_WORKTOOL_ALLOCATIONS.WORKTOOL_ID',
'    AND SEC_HD_WORKTOOL_ALLOCATIONS.DIVISION_ID = SEC_HD_DIVISIONS.DIVISION_ID',
'    AND SEC_HD_WORKTOOL_ALLOCATIONS.DEPARTMENT_ID = SEC_HD_DEPARTMENTS.DEPARTMENT_ID',
'    AND SEC_HD_WORKTOOL_ALLOCATIONS.DEPARTMENT_ID = SEC_HD_DEPARTMENTS.DEPARTMENT_ID',
'',
'    and (:P6_WORKTOOL_TYPE is NULL or :P6_WORKTOOL_TYPE = SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_TYPE)    ',
'    and (:P6_MANUFACTURER is NULL or :P6_MANUFACTURER = SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_MF)',
'    and ((:P6_ALLOCATION_TYPE is NULL or :P6_ALLOCATION_TYPE = SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_TYPE or :P6_ALLOCATION_TYPE = SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_USE))',
'    and (:P6_FLOOR is NULL or :P6_FLOOR = SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_FLOOR)',
'    and (:P6_DEPARTMENT is NULL or :P6_DEPARTMENT = SEC_HD_DEPARTMENTS.DEPARTMENT_ID)',
'    and (:P6_DIVISION is NULL or :P6_DIVISION = SEC_HD_DIVISIONS.DIVISION_ID)',
'    and (:P6_STATUS is NULL or :P6_STATUS = SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_STATUS)',
'    ',
'    and (:P6_SEARCH is null',
'           or instr(upper(SEC_HD_TICKETS.STAFF_ID),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_STATUS),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_TYPE),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_FLOOR),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_TYPE),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_DIVISIONS.DIVISION_NAME),upper(:P6_SEARCH))>0',
'           or instr(upper(SEC_HD_DEPARTMENTS.DEPARTMENT_NAME),upper(:P6_SEARCH))>0',
'           )',
'',
'order by',
'    case :P6_SORT',
'    when ''LASTUPDATED'' then localtimestamp - SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_STATUS_DATE',
'    when ''DATEALLOCATED'' then localtimestamp - SEC_HD_WORKTOOL_ALLOCATIONS.ALLOCATION_DATE',
'    when ''DATEPURCHASED'' then localtimestamp - SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_PURCHASE_DATE',
'    when ''DATEMANUFACTURED'' then localtimestamp - SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_MF_DATE',
'    else localtimestamp - SEC_HD_WORKTOOL_INFORMATION.WORKTOOL_STATUS_DATE',
'    end'))
