'  ',
'  TICKET_DESCRIPTION event_desc,',
'  ',
'  TICKET_ASSIGNED_TO event_extra,',
'  ',
'  case TICKET_STATUS ',
'    when ''PENDING'' then ''fa fa-clock-o''',
'    when ''RESOLVED'' then ''fa fa-check-circle-o''',
'    when ''RETRIEVED'' then ''fa fa-exclamation-circle''',
'    when ''OUT FOR REPAIRS'' then ''fa fa-exclamation-triangle''',
'    when ''BACK FROM REPAIRS'' then ''fa fa-exception''',
'  end event_icon,',
'',
'  case TICKET_STATUS ',
'    when ''RESOLVED'' then ''is-new''',
'    when ''PENDING'' then ''is-removed''',
'    when ''RETRIEVED'' then ''is-updated''',
'    when ''OUT FOR REPAIRS'' then ''is-mine-out''',
'    when ''BACK FROM REPAIRS'' then ''is-mine-back''    ',
